package com.z.player.help;import android.app.Activity;import android.text.TextUtils;import android.util.Log;import java.util.ArrayList;import java.util.List;/** * 作者: zyl on 2019/7/1 20:27 * 描述: ${DESCRIPTION} * 修改人: * 修改描述: * 修改日期: */public class ActivityCollector {    private static final String TAG = ActivityCollector.class.getName();    public static List<Activity> activities = new ArrayList<>();    public static void addActivity(Activity activity) {        Log.d(TAG, "---> addActivity: ---> activity=" + activity.getClass().getName());        activities.add(activity);    }    public static void removeActivity(Activity activity) {        activities.remove(activity);    }    public static void finishAll() {        for (Activity activity : activities) {            if (!activity.isFinishing()) {                Log.d(TAG, "---> finishAll: ---> activity=" + activity.getClass().getName());                activity.finish();            }        }    }    public static void finishActivity(Class<? extends Activity> clazz) {        try {            for (Activity activity : activities) {                if (TextUtils.equals(activity.getClass().getName(), clazz.getName())) {                    removeActivity(activity);                    if (!activity.isFinishing()) {                        activity.finish();                    }                }            }        } catch (Exception e) {            e.printStackTrace();        }    }}